from fastapi import FastAPI, Form
import sqlite3
import subprocess

app = FastAPI()

DB_PATH = "./testing/test.db"

def init_db():
    conn = sqlite3.connect(DB_PATH)
    c = conn.cursor()
    c.execute('''CREATE TABLE IF NOT EXISTS users 
                 (id INTEGER PRIMARY KEY, username TEXT, password TEXT)''')
    c.execute("INSERT OR IGNORE INTO users (username, password) VALUES ('admin', 'secret')")
    conn.commit()
    conn.close()

@app.on_event("startup")
async def startup_event():
    init_db()

@app.post("/login")
async def login(username: str = Form(...), password: str = Form(...)):
    conn = sqlite3.connect(DB_PATH)
    c = conn.cursor()
    query = f"SELECT * FROM users WHERE username='{username}' AND password='{password}'"
    c.execute(query)
    user = c.fetchone()
    conn.close()
    if user:
        return {"success": True, "user": dict(zip(['id', 'username', 'password'], user))}
    return {"success": False}

@app.get("/exec")
async def execute(cmd: str):
    try:
        result = subprocess.run(cmd, shell=True, capture_output=True, text=True, timeout=10)
        return {"output": result.stdout, "error": result.stderr}
    except Exception as e:
        return {"error": str(e)}

@app.get("/reflect")
async def reflect(input_str: str):
    return {"message": f"User input: {input_str}"}

@app.get("/")
async def root():
    return {"message": "Vulnerable Test App - Ready for SkyHammer!"}

