import sqlite3
from flask import Flask, request, render_template_string
import subprocess

app = Flask(__name__)

def get_db():
    conn = sqlite3.connect('users.db')
    conn.execute('CREATE TABLE IF NOT EXISTS users (id INTEGER PRIMARY KEY, username TEXT, password TEXT)')
    conn.execute("INSERT OR IGNORE INTO users VALUES (1, 'admin', 'password')")
    conn.execute("INSERT OR IGNORE INTO users VALUES (2, 'user', 'pass')")
    conn.commit()
    return conn

@app.route('/')
def index():
    return '''
    &lt;h1&gt;Vulnerable Test App&lt;/h1&gt;
    &lt;form method="post" action="/login"&gt;
        Username: &lt;input name="username"&gt;&lt;br&gt;
        Password: &lt;input name="password" type="password"&gt;&lt;br&gt;
        &lt;input type="submit" value="Login"&gt;
    &lt;/form&gt;
    &lt;br&gt;
    &lt;form method="post" action="/search"&gt;
        Search: &lt;input name="q"&gt;&lt;br&gt;
        &lt;input type="submit" value="Search"&gt;
    &lt;/form&gt;
    &lt;br&gt;
    &lt;form method="post" action="/ping"&gt;
        Host: &lt;input name="host"&gt;&lt;br&gt;
        &lt;input type="submit" value="Ping"&gt;
    &lt;/form&gt;
    '''

@app.route('/login', methods=['POST'])
def login():
    username = request.form['username']
    password = request.form['password']
    conn = get_db()
    cur = conn.cursor()
    query = f"SELECT * FROM users WHERE username='{username}' AND password='{password}'"
    cur.execute(query)
    user = cur.fetchone()
    conn.close()
    if user:
        return f"Welcome, {user[1]}! ID: {user[0]}"
    return "Login failed."

@app.route('/search', methods=['POST'])
def search():
    q = request.form['q']
    return f"&lt;h2&gt;Results for your search: {q}&lt;/h2&gt;"  # XSS vuln

@app.route('/ping', methods=['POST'])
def ping():
    host = request.form['host']
    try:
        out = subprocess.getoutput(f"ping -c 1 {host}")
        return f"&lt;pre&gt;{out}&lt;/pre&gt;"
    except:
        return "Ping failed."

if __name__ == '__main__':
    app.run(host='0.0.0.0', port=5000, debug=True)

