from fastapi import FastAPI, Form, Request
from fastapi.responses import HTMLResponse
import sqlite3

app = FastAPI()

def get_db():
    conn = sqlite3.connect('./testing/users.db')
    conn.row_factory = sqlite3.Row
    return conn

@app.get("/setup")
def setup():
    conn = get_db()
    c = conn.cursor()
    c.execute("CREATE TABLE IF NOT EXISTS users (username TEXT, password TEXT)")
    c.execute("INSERT OR IGNORE INTO users VALUES ('admin', 'secret')")
    conn.commit()
    conn.close()
    return {"status": "DB setup complete"}

@app.get("/", response_class=HTMLResponse)
async def index(request: Request):
    return """
    &lt;h1&gt;Vulnerable App&lt;/h1&gt;
    &lt;form method="post" action="/login"&gt;
        Username: &lt;input name="username"&gt;&lt;br&gt;
        Password: &lt;input name="password" type="password"&gt;&lt;br&gt;
        &lt;input type="submit" value="Login"&gt;
    &lt;/form&gt;
    &lt;br&gt;
    &lt;a href="/search?q=test"&gt;Search Example&lt;/a&gt;
    """

@app.post("/login")
async def login(username: str = Form(...), password: str = Form(...)):
    conn = get_db()
    c = conn.cursor()
    query = f"SELECT * FROM users WHERE username='{username}' AND password='{password}'"
    c.execute(query)
    user = c.fetchone()
    conn.close()
    if user:
        return f"Welcome, {user['username']}! You logged in successfully."
    return "Invalid credentials."

@app.get("/search")
async def search(q: str = ""):
    # XSS vulnerability - no escaping
    return HTMLResponse(f"""
    &lt;h2&gt;Search results for: &lt;b&gt;{q}&lt;/b&gt;&lt;/h2&gt;
    &lt;p&gt;No results found.&lt;/p&gt;
    """)

if __name__ == "__main__":
    import uvicorn
    uvicorn.run("vulnerable_app:app", host="0.0.0.0", port=5000, reload=True)
